package external.basic

object ArithmeticOperator {

  def main(args: Array[String]): Unit = {
    var a = 50;
    var b = 50;

    // arithmetic operator
    //addition
    println("addition is:" + (a + b))
    // subtraction
    println("subtraction is:" + (a - b))
    // multiplication
    println("multiplication is:" + (a * b))
    //division
    println("division is:" + (a / b))
    //modules
    println("modules is:" + (a % b))


  }
}
