package external.basic

object LogicalOperator {

  def main(args: Array[String]): Unit = {
    //logical operator
    var c = false
    var d = true
    //logical and
    println("c and d :" + (c && d))
    //logical not
    println("c and d not :" + !(c && d))
    //logical or
    println("c or d :" + (c || d))

    //assignment operator


  }
}
