package external.basic

object RelationOperator {

  def main(args: Array[String]): Unit = {
    var a = 50;
    var b = 50;
    //relational operator
    //equality
    println("equality :" + (a == b))
    //not equality
    println("not equality :" + (a != b))
    //greater
    println("greater :" + (a > b))
    // less
    println("less :" + (a < b))
    //greater then equal
    println("greater then equal :" + (a >= b))
    //less then equal
    println("less then equal :" + (a <= b))

  }
}
